import React from "react";
import {
  Text,
  Flex,
  Box,
  VStack,
  HStack,
  Icon,
  Badge,
  Spacer,
  Button,
  Center,
  Divider,
  ScrollView,
  Checkbox,
} from "native-base";
import { Linking, Platform } from "react-native";

import { Entypo, SimpleLineIcons, AntDesign } from "@expo/vector-icons";
import OrderDetail from "../../../_shared/libs/OrderDetail";

interface OrderDeliveredProps {
  item: any;
}

const OrderDelivered: React.FC<OrderDeliveredProps> = ({ item }) => {
  const [groupValues, setGroupValues] = React.useState([]);

  const makeCall = () => {
    let phoneNumber = "";

    if (Platform.OS === "android") {
      phoneNumber = "tel:${08080219726}";
    } else {
      phoneNumber = "telprompt:${08080219726}";
    }

    Linking.openURL(phoneNumber);
  };
  return (
    <ScrollView flex={1} width="100%" mt="60px">
      <VStack px="40px">
        <OrderDetail item={{}} />
      </VStack>
      <Divider my="3" />
      <VStack px="40px" space={2}>
        <Text>SELECT PAYMENT METHOD</Text>
        <Text>kindly confirm method of payment used by customer</Text>
        <Checkbox.Group
          onChange={setGroupValues}
          value={groupValues}
          accessibilityLabel="choose numbers"
        >
          <Checkbox value="one" my={4}>
            Cash
          </Checkbox>
          <Checkbox value="two">Transfer</Checkbox>
        </Checkbox.Group>
        <VStack mt={4} space={6}>
           <Text>BANK DETAILS</Text>
           <HStack>
               <Text>Bank Name: </Text>
               <Text>First Bank</Text>
           </HStack>
           <HStack>
               <Text>Account Holder's Name: </Text>
               <Text>Sendme</Text>
           </HStack>
           <HStack>
               <Text>Account Number: </Text>
               <Text>901283930</Text>
           </HStack>
        </VStack>
      </VStack>
    </ScrollView>
  );
};

export default OrderDelivered;

