import React from "react";
import {
  Text,
  Box,
  VStack,
  HStack,
  Divider,
  Button,
  Center,
  Input,
  IconButton,
} from "native-base";

import RidersCard from "../../../_shared/libs/Riders_MoneyFlow_Card";
import SendMe from "../../../assets/sendmelogo.svg";
import { Entypo, SimpleLineIcons, AntDesign } from "@expo/vector-icons";
import { MaterialIcons } from "@expo/vector-icons";

import Svg, { Circle, Rect } from "react-native-svg";

const Login = () => {
  const [show, setShow] = React.useState(false);
  const handleShowPassword = () => setShow(!show);

  return (
    <Box w="100%" flex={1} backgroundColor="#F6F5F5" px="8" pt="60px">
      <Center mt={12}>
        {/* <SendMe />  */}
        Logo isHere
      </Center>
      <Box mt={16}>
        <HStack>
          <Text fontWeight="extrabold" fontSize="bx" color="col.default">
            Log
          </Text>
          <VStack>
            <Text
              pl="5px"
              fontWeight="extrabold"
              fontSize="bx"
              color="col.default"
            >
              In
            </Text>
            <Divider borderRadius="4" thickness="5" bg="col.500" />
          </VStack>
        </HStack>
        <Text mt={4} fontSize="bs" color="col.greybold" lineHeight="19px">
          Confirm your details and create password
        </Text>
      </Box>

      <VStack space={10} mt={12} mb={24}>
        <Box>
          <Text
            pl="2"
            fontWeight="semibold"
            fontSize="bs"
            lineHeight="19px"
            color="col.greylight"
          >
            Full Name
          </Text>
          <Input
            type="text"
            variant="underlined"
            h={8}
            px="2"
            fontSize="lg"
            fontWeight="semibold"
            lineHeight="23px"
            color="col.default"
          />
        </Box>

        <Box>
          <Text
            pl="2"
            fontWeight="semibold"
            fontSize="bs"
            lineHeight="19px"
            color="col.greylight"
          >
            Password
          </Text>

          <Input
            type={show ? "text" : "password"}
            variant="underlined"
            h={8}
            px="2"
            fontSize="lg"
            fontWeight="semibold"
            lineHeight="23px"
            color="col.default"
            // InputRightElement={
            //     <Icon
            //       as={<MaterialIcons name="visibility-off" />}
            //       size={5}
            //       mr="2"
            //       color="muted.400"
            //     />
            //   }
            InputRightElement={
              <IconButton
                size="xs"
                rounded="none"
                w="1/6"
                h="full"
                _icon={{
                  as: MaterialIcons,
                  name: show?'visibility-on':'visibility-on'
                }}
                onPress={handleShowPassword}
              />
            }
          />
        </Box>
      </VStack>
      <Button
        bgColor={"col.500"}
        height="56px"
        borderRadius="12px"
        _text={{
          fontWeight: "semibold",
          fontSize: "lg",
          lineHeight: "22.59px",
        }}
      >
        Log In
      </Button>
    </Box>
  );
};

export default Login;
