import React from "react";
import {
  Text,
  Link,
  HStack,
  Center,
  Heading,
  Switch,
  useColorMode,
  NativeBaseProvider,
  extendTheme,
  VStack,
  Code,
  Box,
  Button,
  Spacer,
  Icon,
} from "native-base";
import NativeBaseIcon from "@src/components/NativeBaseIcon";
import { DashMenu } from "@src/../assets/IconComponents/DashMenu";

//test imports auth screens
import Login from "./AuthScreens/Login";
import SignUp from "./AuthScreens/SignUp";
import Verification from "./AuthScreens/Verification";
import OrderDetail from "@src/_shared/libs/OrderDetail";
import OrderStartsArrived from "./Orders/OrderStartsArrived";
import OrderDelivered from "./Orders/OrderDelivered";
import MapScreen from "./MapScreens";
import BatchCardX from "@src/_shared/libs/BatchCardX";
import OrderCard from "@src/_shared/libs/OrderCard";
import BatchCardXList from "@src/_shared/libs/BatchCardXList";

// Define the config
const config = {
  useSystemColorMode: false,
  initialColorMode: "dark",
};

const DrawerScreensLayout = ({ children }) => {
  return (
    <Box flex={1} safeArea mx={3}>
      <VStack>
        <DashMenu fill={"#596273"} />
        <HStack>
          <Text
            fontWeight="bold"
            color="col.default"
            fontSize="27px"
            lineHeight="34px"
          >
            Orders
          </Text>
          <Spacer />
          <VStack>
            <Switch
              defaultIsChecked
              offTrackColor="col.400"
              onTrackColor="col.800"
              onThumbColor="col.500"
              offThumbColor="col.white"
            />
            <Text>O</Text>
          </VStack>
        </HStack>
        <Text>See all your orders here</Text>
        <Box>{children}</Box>
      </VStack>
    </Box>
  );
};

// extend the theme
export const theme = extendTheme({ config });

export default function Home({ navigation }) {
  return (
    <DrawerScreensLayout>
      <BatchCardXList />
    </DrawerScreensLayout>
  );
}

